sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;

	c.selectItem = function (index) {
		var id = c.data.notes[index].sys_id;

		$rootScope.noteID = id;
		$rootScope.$emit("selectNote", id);
	}

	$rootScope.$on("noteUpdated", function (event, data) {
		var updatedNoteIndex = c.data.notes.findIndex(function(note) {
			return note.sys_id === data.noteID;
		});

		c.data.notes[updatedNoteIndex].title = data.noteTitle;
		c.data.notes[updatedNoteIndex].note = data.noteBody;
	});
 };

sp_widget.script:
(function() {
	data.notes = [];

	var notes = new GlideRecord("x_snc_createnotes_note");

	notes.addQuery("user", gs.getUserID());
	notes.orderByDesc("sys_created_on");
	notes.query();

	while(notes.next()) {
		var note = {};

		$sp.getRecordDisplayValues(note, notes, "number,title,sys_id");
		note.note = notes.getValue("note").slice(0, 20);

		data.notes.push(note);
	}
})();
